import React, { Fragment, useState } from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { login } from "../../actions/auth";
import { Helmet } from "react-helmet";
import { setAlert } from "../../actions/alert";

const Login = ({ login, isAuthenticated, setAlert }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });

  const { email, password } = formData;
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });
  let disable = false;

  const onSubmit = async e => {
    e.preventDefault();

    if (disable === false) {
      if (!password) {
        setAlert("password required", "danger");
      } else if (!email) {
        setAlert("email is required", "danger");
      } else if (isAuthenticated) {
        return <Redirect to="/dashboard" />;
      } else {
        login(email, password);
      }
      disable = true;
      setTimeout(function() {
        disable = false;
      }, 5000);
      console.log(disable);
    } else {
      console.log("wait 5 seconds");
    }
  };

  if (isAuthenticated) {
    return <Redirect to="/dashboard" />;
  }

  return (
    <Fragment>
      <Helmet bodyAttributes={{ style: "background-color: black" }} />

      <form
        style={{ marginLeft: "auto", marginRight: "auto", width: "300px" }}
        className="form"
        onSubmit={e => onSubmit(e)}
      >
        <h1 style={{ marginTop: "150px" }} className="large text-primary">
          Login
        </h1>

        <div className="form-group">
          <input
            style={{
              marginTop: "20px;",
              marginLeft: "auto",
              marginRight: "auto",
              width: "300px"
            }}
            type="email"
            placeholder="Email Address"
            name="email"
            value={email}
            onChange={e => onChange(e)}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            name="password"
            value={password}
            onChange={e => onChange(e)}
            style={{
              marginTop: "20px;",
              marginLeft: "auto",
              marginRight: "auto",
              width: "300px"
            }}
          />
        </div>

        <input
          type="submit"
          className="btn btn-primary"
          id="button"
          value="Login"
        />
        <p className="my-1">
          Don't have an account? <Link to="/register">Sign Up</Link>
        </p>
      </form>
    </Fragment>
  );
};

Login.propTypes = {
  login: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool,
  setAlert: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
});
export default connect(
  mapStateToProps,
  { login, setAlert }
)(Login);
