import React, { Component, useState } from "react";

import axios from 'axios'
import { Helmet } from "react-helmet";
// eslint-disable-next-line no-unused-vars
// import axios from "axios";
// import { connect } from "react-redux";
// import PropTypes from "prop-types";
// import { getContacts } from "../actions/contactActions";
// import moduleName from "../actions/contactActions";
// import uuid from "uuid";
// import { addContact } from "../actions/contactActions";

// import { deleteContact } from "../actions/contactActions";
const formStyle = {
  width: '50%',
  margin: '200px 25% auto 25%',
  color: 'white'

}

const buttonStyle = {
  backgroundColor: 'lightGrey',
  border: '1px solid blue',
  marginTop: '76px',
  marginLeft: '45%',
  marginRight: '45%',
  width: '10%'

}

function Contact() {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [message, setMessage] = useState('')

  const handleClick = (e) => {
    e.preventDefault();

    if (e.target.id === 'name') {
      setName(e.target.value)
    } else if (e.target.id === 'email') {
      setEmail(e.target.value)
    } else if (e.target.id === 'message') {
      setMessage(e.target.value)
    }
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    const dataToSubmit = {
      name: name,
      email: email,
      name: name
    }
    axios.post('api/sendMail', dataToSubmit)

  }

  http//loalhost: 3000

  return (
    <div className='Contact'>
      <Helmet bodyAttributes={{ style: "background-color: black" }} />
      <header className='Contact-Header'>
        <form style={formStyle} onSubmit={handleSubmit}>
          <input id='name' placeholder='Name' value={name} onChange={handleClick} /> <br></br>
          <input id='email' placeholder='Email' value={email} onChange={handleClick} /> <br></br> <br></br>
          <input id='message' placeholder='Message' value={message} onChange={handleClick} />
          <br></br><br></br>
          <button style={buttonStyle} onClick={handleSubmit}>Send Email</button>
        </form>
      </header>
    </div>
  )
}

export default Contact;
