import React, { Component } from "react";
// import EmailForm from "./form/EmailForm";
import { Helmet } from "react-helmet";
// eslint-disable-next-line no-unused-vars
// import axios from "axios";
// import { connect } from "react-redux";
// import PropTypes from "prop-types";
// import { getContacts } from "../actions/contactActions";
// import moduleName from "../actions/contactActions";
// import uuid from "uuid";
// import { addContact } from "../actions/contactActions";

// import { deleteContact } from "../actions/contactActions";
const formStyle = {
  width: '50%',
  margin: '200px 25% auto 25%',
  color: 'white'

}

const buttonStyle = {
  backgroundColor: 'lightGrey',
  border: '1px solid blue',
  marginTop: '76px',
  marginLeft: '45%',
  marginRight: '45%',
  width: '10%'

}

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }



  render() {
    return (
      <div className="App">
        <Helmet bodyAttributes={{ style: "background-color: black" }} />
        <form style={formStyle} id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input type="text" className="form-control" value={this.state.name} onChange={this.onNameChange.bind(this)} />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Email address</label>
            <input type="email" className="form-control" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)} />
          </div>
          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea className="form-control" rows="5" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
          </div>

        </form>
        <button type="submit" style={buttonStyle} className="btn btn-primary">Submit</button>
      </div>
    );
  }

  onNameChange(event) {
    this.setState({ name: event.target.value })
  }

  onEmailChange(event) {
    this.setState({ email: event.target.value })
  }

  onMessageChange(event) {
    this.setState({ message: event.target.value })
  }

  handleSubmit(e) {
    e.preventDefault();

    fetch('http://localhost:3002/send', {
      method: "POST",
      body: JSON.stringify(this.state),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
    }).then(
      (response) => (response.json())
    ).then((response) => {
      if (response.status === 'success') {
        alert("Message Sent.");
        this.resetForm()
      } else if (response.status === 'fail') {
        alert("Message failed to send.")
      }
    })
  }
}
export default Contact;
