import React, { Fragment, useState } from "react";
import { Helmet } from "react-helmet";

import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { setAlert } from "../../actions/alert";
import { register } from "../../actions/auth";
import PropTypes from "prop-types";

const Register = ({ setAlert, register, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: ""
  });

  const { name, email, password, password2 } = formData;
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    if (password !== password2) {
      setAlert("passwords do not match", "danger");
    } else {
      register({ name, email, password });
    }
  };

  const formStyle = {
    marginTop: "70px",
    borderRadius: ".8em",
    color: "lightgrey"
  };
  const input = {
    height: "50px"
  };

  if (isAuthenticated) {
    return <Redirect to="/dashboard" />;
  }

  return (
    <Fragment>
      <Helmet bodyAttributes={{ style: "background-color: black" }} />
      <div style={{ marginLeft: "30%", marginRight: "30%" }}>
        <div className="card">
          <form className="form" onSubmit={e => onSubmit(e)}>
            <div style={formStyle} className="form-group">
              <label className="lightgrey">Name:</label>

              <input
                type="text"
                placeholder="Name"
                name="name"
                value={name}
                onChange={e => onChange(e)}
                style={input}
              />
            </div>
            <div className="form-group">
              <label className="lightgrey">Email address:</label>
              <input
                type="email"
                placeholder="Email Address"
                name="email"
                value={email}
                onChange={e => onChange(e)}
                style={input}
              />
            </div>
            <div className="form-group">
              <label className="lightgrey">Password</label>
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={password}
                onChange={e => onChange(e)}
                style={input}
              />
            </div>
            <div className="form-group">
              <label className="lightgrey">Retype Your Password</label>
              <input
                type="password"
                placeholder="Confirm Password"
                name="password2"
                value={password2}
                onChange={e => onChange(e)}
                style={input}
              />
            </div>
            <input type="submit" className="btn btn-primary" value="Register" />
          </form>
          {/* <p>
            Alread have an accout? <Link to="/login">Sign In</Link>
          </p> */}
        </div>
      </div>
    </Fragment>
  );
};

Register.propTypes = {
  setAlert: PropTypes.func.isRequired,
  register: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
});

export default connect(
  mapStateToProps,
  { setAlert, register }
)(Register);
